char showMenu()
{
	printf("1. Execute the entire project\n");
	printf("2. Execute AssetLoader\n");
	printf("Pulsar 'q' para salir\n");
	printf("\n");
	printf("Option: ");
	fflush(stdout);
	char linea[MAX_LINE];
	fgets(linea, MAX_LINE, stdin);
	clearIfNeeded(linea, MAX_LINE);
	return *linea;
}

int main() {
	console_logo();
	printf("Welcome to the REAL engine!\n");
	char response;
	do {
		response = showMenu();
		switch (response)
		{
		case '1':
			//printf(system("dir ..\\x64\\Debug"));
			printf("Loading AssetLoader\n");
			system("start AssetLoader.exe");
			Sleep(1000);
			printf("AssetLoader loaded\n");
			printf("Loading ShaderCompilerServer\n");
			system("start ShaderCompilerServer.exe");
			Sleep(1000);
			printf("ShaderCompilerServer loaded\n");
			printf("Loading OpenGLGraphics\n");
			system("OpenGLGraphics.exe");
			Sleep(1000);
			printf("start OpenGLGraphics loaded\n");
			return 0;
		case '2':
			system("AssetLoader.exe");
			return 0;
		default:
			print_Linus();
		}
	} while (response != 'q');
}